if not v then v = {} endif not AQUARIA_VERSION then dofile("scripts/entities/entityinclude.lua") enddofile(appendUserDataPath("_mods/Elemental/scripts/inc_util.lua"))v.x = 0v.y = 0v.scale = 1function init(me)    setupEntity(me, "missingimage")    entity_setEntityType(me, ET_NEUTRAL)    entity_setState(me, STATE_IDLE)    entity_setUpdateCull(me, -1)    esetv(me, EV_LOOKAT, 0)    entity_setAllDamageTargets(me, false)    entity_setSpiritFreeze(me, false) -- keep updating    entity_setInvincible(me, true)endfunction postInit(me)endfunction update(me, dt)    local x, y = v.toWorldFromWindow(v.x, v.y)    entity_setPosition(me, x, y)        local sc = v.scale / v.getZoom()    entity_scale(me, sc, sc)endfunction msg(me, s, x)    if     s == "scale" then v.scale = x / 1000    elseif s == "x"     then v.x = x    elseif s == "y"     then v.y = x    endendfunction damage(me, attacker, bone, damageType, dmg)    return falseendfunction activate(me)endfunction animationKey(me, key)endfunction enterState(me)end function exitState(me)endfunction hitSurface(me)endfunction songNote(me, note)endfunction songNoteDone(me, note)endfunction song(me, song)endfunction shiftWorlds(me, old, new)end